functions.raw_rules:
/**
    @notice A distinct Uniform Resource Identifier (URI) for a given token.
    @dev URIs are defined in RFC 3986.
    The URI MUST point to a JSON file that conforms to the "ERC-1155 Metadata URI JSON Schema".        
    @return URI string
*/

extract_debug.throw:
[
  {
    "if": "returned URI is not a valid RFC 3986 URI",
    "throw": false
  },
  {
    "if": "returned URI does not point to a JSON file that conforms to the ERC-1155 Metadata URI JSON Schema",
    "throw": false
  }
]

extract_debug.return:
[
  {
    "ret_value": "URI string that points to a JSON file conforming to the ERC-1155 Metadata URI JSON Schema"
  }
]
